  pin:true,
  animation:tween,
  scrub:4,
  invalidateOnRefresh:true,
  markers:false
})
    // cleanup
    return () => ScrollTrigger.killAll();
  }, []);

  return (
    <div className="racesWrapper">
      <div className="races" ref={racesRef}>
