import React, { useEffect, useRef } from "react";
import "./Anim.css";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import team1 from '../img/Team_Dylan___Mobile.png';
import team2 from '../img/Team_Dylan___Mobile.png';
import team3 from '../img/Team_Dylan___Mobile.png';

gsap.registerPlugin(ScrollTrigger);

const Anim = () => {
  const racesRef = useRef(null);

  useEffect(() => {
    const races = racesRef.current;
    if (!races) return; // safety check

    const getScrollAmount = () => {
      let racesWidth = races.scrollWidth;
      return -(racesWidth - window.innerWidth);
    };

    const tween = gsap.to(races, {
      x: getScrollAmount,
      duration: 3,
      ease: "none",
    });

    ScrollTrigger.create({
      trigger: ".racesWrapper",
      start: "middle 100%",
      end: () => `+=${getScrollAmount()}`,
      pin: true,
      scrub: true,
      animation: tween,
    });

    // cleanup
    return () => ScrollTrigger.killAll();
  }, []);

  return (
    <div className="racesWrapper">
      <div className="races" ref={racesRef}>
        {/* Your items go here */}
        <div className="item"><img src={team1} alt="team1" /></div>
        <div className="item">Item 2</div>
        <div className="item">Item 3</div>
      </div>
    </div>
  );
};

export default Anim;
