import React, { useEffect, useRef } from "react";
import "./Home.css";
import Nav from "../components/Nav.jsx";
import hero_img from "../img/gemenini_hero.png";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import Case_img from '../'

gsap.registerPlugin(ScrollTrigger);

gsap.registerPlugin(ScrollTrigger);

const  Home= () => {

console.clear();

gsap.registerPlugin(ScrollTrigger);

const items = gsap.utils.toArray(".item");
const tl = gsap
  .timeline({
    defaults: {
      ease: "none",
      duration: 1
    },
    scrollTrigger: {
      trigger: ".wrapper",
      start: "top top",
      end: "+=200%",
      pin: true,
      scrub: true,
      markers: false
    }
  })
  .to({}, { duration: 0.12 });

items.forEach((item) => {
  const body = item.querySelector(".body");
  const hasChild = body.dataset.haschild;
  tl.to(
    body,
    {
      height: "auto"
    },
    "+=0.5"
  );
  if (hasChild) {
    const cardsContainer = body.querySelector(".cards-container");
    tl.to(
      cardsContainer,
      {
        x: cardsContainer.clientWidth - cardsContainer.scrollWidth,
        duration: 4
      },
      "+=0.35"
    );
  } else {
    const paragraphs = body.querySelectorAll("p");
    tl.to(
      paragraphs,
      {
        opacity: 1,
        stagger: 0.25
      },
      "+=0.35"
    );
  }
  tl.to(
    body,
    {
      height: 0
    },
    "+=0.5"
  );
});
tl.to({}, { duration: 0.25 });








    return ( <>

      <section className='s1_Home'>
      <Nav/>

      <div className='div_hero'>
        <img className='heroimg' src={hero_img} alt='hero_img'/>

        <div className='div_cntnt_hero'>
        <h1>Measure Twice,<br/>Cut once</h1>
        </div>
      </div>

      </section>



      <div class="item" className="item_nn">
      <div class="header" className="heder">
        <h3 className="h_ani1">THE PROOF IS IN THE PUDDING</h3>
        <p className="p_ani1">A stack of stuff weâ€™ve been working on lately.</p>
      </div>
      <div class="body" data-haschild="true">
        <div class="content">
          <div class="horizontal center">
            <div class="cards-container">
              <div class="card center gradient-green" className="scr1" >
                <div>
                  <img src={Case_img} alt="case_img" />

                </div>
              </div>
              <div class="card center gradient-blue">Card 2</div>
              <div class="card center gradient-purple">Card 3</div>
              <div class="card center gradient-orange">Card 4</div>
              <div class="card center gradient-green-2">Card 5</div>
              <div class="card center gradient-blue-2">Card 6</div>
            </div>
          </div>
        </div>
      </div>
    </div>



      </>
     );
}

export default Home;
