import React, { useEffect, useRef } from "react";
import "./Anim.css";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";



const Anim = () => {

const races = document.querySelector(".races");
console.log(races.offsetWidth)

function getScrollAmount() {
  let racesWidth = races.scrollWidth;
  return -(racesWidth - window.innerWidth);
}

const tween = gsap.to(races, {
  x: getScrollAmount,
  duration: 3,
  ease: "none",
});


ScrollTrigger.create({
  trigger:".racesWrapper",
  start:"top 20%",
  end: () => `+=${getScrollAmount() * -1}`,
  pin:true,
  animation:tween,
  scrub:1,
  invalidateOnRefresh:true,
  markers:true
})


    return ( <>
        <div class="space-50vh lightBG"></div>

<div class="racesWrapper">
  <div class="races">
    <h2>Monaco</h2>
    <h2>Austria</h2>
    <h2>Hungary</h2>
    <h2>Netherlands</h2>
    <h2>Japan</h2>
  </div>
</div>

<div className="space-100vh lightBG"></div>
<div className="brand" ><link href="https://www.creativecodingclub.com/bundles/creative-coding-club?src=cdpnhorscrll" target="_blank"><img src="https://assets.codepen.io/32887/logo-ill.svg" width="300" alt="" /></link></div>

</>
    )
}

export default Anim;
